, cal_month
, cal_day
, cal_time
, cal_memo
, time_builder
)VALUES(
#{phone}
, #{calYear}
, #{calMonth}
, #{calDay}
, #{calTime}
, #{calMemo} 
, #{timeBuilder}
)

mapper.select:
SELECT * from calendar_tbl
WHERE cal_year = #{calYear}
AND cal_month = #{calMonth}
AND phone = #{phone}

SELECT * FROM calendar_tbl
WHERE cal_year = #{calYear}
AND cal_month = #{calMonth}
AND cal_day = #{calDay}
AND phone = #{phone}

mapper.update:
